mapper.update:
UPDATE ST_SHOES_INFO
SET
    BATTERY = #{battery}
    , UPD_DATE = DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
WHERE
    SHOES_NO = #{shoesNo}

mapper.insert:
INSERT INTO ST_ACTIVE_RANGE (
    RANGE_NAME
    , SHOES_NO
    , RANGE_ADDRESS
    , LATITUDE
    , LONGITUDE
    , RADIUS
    , REG_DATE
    , UPD_DATE
    , REG_USER
)
    ( SELECT
        #{rangeName}
        , SI.SHOES_NO
        , #{rangeAddress}
        , #{latitude}
        , #{longitude}
        , #{radius}
        , DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
        , DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
        , #{regUser}
      FROM ST_SHOES_INFO SI LEFT JOIN ST_SHOES_GUARD SSG on SI.SHOES_NO = SSG.SHOES_NO
      WHERE SSG.SHOES_NO = #{shoesNo} AND SSG.MASTER_GUARD_NO = #{guardNo}
    )

mapper.delete:
DELETE AR FROM ST_ACTIVE_RANGE AR
    LEFT JOIN ST_SHOES_INFO SI ON AR.SHOES_NO = SI.SHOES_NO
    LEFT JOIN ST_SHOES_GUARD SG ON SI.SHOES_NO = SG.SHOES_NO
WHERE
    RANGE_NO = #{rangeNo}
    AND SG.MASTER_GUARD_NO = #{guardNo}

mapper.insert:
INSERT INTO ST_LOCATION (
    SHOES_NO
    , LATITUDE
    , LONGITUDE
    , STATUS
    , REPORT_DATE
) VALUES(
    #{shoesNo}
    , #{latitude}
    , #{longitude}
    , #{status}
    , DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
)

INSERT INTO ST_DANGER (
      SHOES_NO
    , LOC_NO
    , LAT
    , LON
    , DANGER_YN
    , REG_DATE
    , UPD_DATE
) VALUES(
    #{shoesNo}
    , #{locNo}
    , #{lat}
    , #{lon}
    , #{dangerYn}
    , DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
    , DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
) ON DUPLICATE KEY UPDATE
    LOC_NO = #{locNo}
    , LAT = #{lat}
    , LON = #{lon}
    , DANGER_YN = #{dangerYn}
    , UPD_DATE = DATE_FORMAT(NOW(), '%Y%m%d%H%i%S')
